import db from "../Kanbas/Database";
import { Navigate, Route, Routes, useParams } from "react-router-dom";
import CourseNavigation from "../Kanbas/Courses/CourseNavigation";
import Modules from "../Kanbas/Courses/Modules";
import Assignments from "../Kanbas/Assignments.json";
import AssignmentEditor from "../Kanbas/Courses/Assignments/AssignmentEditor";
import Home from "../Kanbas/Courses/Home"
import KanbasNavigation from "./KanbasNavigation";

function Courses() {
    const { courseId } = useParams();
    const course = db.courses.find((course) => course.id === courseId);
    return (
        <div class="table-responsive">
            <table>
                <tr>
                    <KanbasNavigation/>
                </tr>
                <tr>
            <h1>Course {course.name}</h1>
            <CourseNavigation />
                </tr>
                <tr>
            <div>
                <div className="overflow-y-scroll position-fixed bottom-0 end-0">
                    <Routes>
                        <Route path="/" element={<Navigate to="Home" />} />
                        <Route path="Home" element={<Home/>} />
                        <Route path="Modules" element={<Modules/>} />
                        <Route path="Assignments" element={<Assignments/>} />
                        <Route path="Assignments/:assignmentId"
                               element={<AssignmentEditor/>}/>
                        <Route path="Grades" element={<h1>Grades</h1>} />
                    </Routes>
                </div>
            </div>
                </tr>
            </table>
        </div>
    );
}
export default Courses;
