import { Link, useLocation } from "react-router-dom";
import "react-icons"
function KanbasNavigation() {
    const links = ["Account", "Dashboard", "Courses", "Inbox", "Groups", "Calendar", "Help"];
    const icons = ["fas fa-user fa-2x", "fas fa-clock fa-2x", "fas fa-book fa-2x",
    "fas fa-envelope fa-2x", "fas fa-user-friends fa-2x", "fas fa-calendar fa-2x",
                   "fas fa-info-circle fa-2x"]
    const { pathname } = useLocation();
    return (
        <div className="list-group" style={{ width: 150 }}>
            {links.map((link, index) => (
                <Link
                    key={index}
                    to={`/Kanbas/${link}`}
                    className={`list-group-item ${pathname.includes(link) && "active"}`}>
                    {link}
                </Link>
            ))}
            {icons.map((link, index) => (
                <Link
                    key={index}
                    to={`/Kanbas/${link}`}
                    className={`list-group-item ${pathname.includes(link) && "active"}`}>
                    {link}
                </Link>
            ))}
        </div>
    );
}
export default KanbasNavigation;
