import db from "../Kanbas/Database";
import {Link, Navigate, Route, Routes, useParams, useLocation} from "react-router-dom";
import CourseNavigation from "../Kanbas/Courses/CourseNavigation";
import Modules from "../Kanbas/Courses/Modules";
import Assignments from "./Assignments";
import AssignmentEditor from "./Assignments/AssignmentEditor";
import Home from "../Kanbas/Courses/Home"
import KanbasNavigation from "./KanbasNavigation";
import Dashboard from "./Dashboard";

function Courses() {
    const {courseId} = useParams();
    const {pathname} = useLocation();
    const course = db.courses.find((course) => course.id === courseId);
    return (
        <div className="table-borderless table-responsive">
            <table className="table" border="0" width="100">
                <tbody>
                <tr>
                    <td valign="top" width="10%">
                        <KanbasNavigation/>
                    </td>
                    <td valign="top">
                        <nav className="nav nav-tabs mt-2">
                            <Link className={`nav-link ${pathname.includes("Dashboard") ? "active" : ""}`}
                                  to="/Kanbas/Dashboard">A3</Link>
                            <Link className={`nav-link ${pathname.includes("hello") ? "active" : ""}`}
                                  to="/hello">Hello</Link>
                            <Link className={`nav-link ${pathname.includes("Kanbas") ? "active" : ""}`}
                                  to="/Kanbas">Kanbas</Link>
                        </nav>
                            <td valign="top" width="10%">
                                <CourseNavigation/>
                            </td>
                            <td valign="top">
                                <div className="overflow-y-scroll position-fixed bottom-0 end-0">
                                    <Routes>
                                        <Route path="/" element={<Navigate to="Home"/>}/>
                                        <Route path="Dashboard" to="/Kanbas/Dashboard"
                                               element={<Dashboard/>}/>
                                        <Route path="Home" element={<Home/>}/>
                                        <Route path="Modules" element={<Modules/>}/>
                                        <Route path="Courses/:courseId/*" element={<Courses/>} />
                                        <Route path="Assignments" element={<Assignments/>}/>
                                        <Route path="Assignments/:assignmentId"
                                               element={<AssignmentEditor/>}/>
                                        <Route path="Grades" element={<h1>Grades</h1>}/>
                                    </Routes>
                                </div>
                            </td>
                    </td>
                </tr>
                </tbody>
            </table>
        </div>
    );
}
export default Courses;
